// Get data from previous nodes
const copychiefOutput = $input.all()[0].json.output;
const startData = $('Start').all()[0].json;

// Parse the webhook payload to get clip ID and brand info
let webhookData = {};
try {
  webhookData = JSON.parse(startData.webhook_payload);
} catch (error) {
  console.log('No webhook payload found, using start data directly');
}

// Parse the CopyChief JSON output
let parsedOutput;
try {
  parsedOutput = JSON.parse(copychiefOutput);
} catch (error) {
  // If parsing fails, create a fallback structure
  parsedOutput = {
    session_id: "fallback-" + Date.now(),
    created_at: new Date().toISOString(),
    contents: {
      content1: {
        type: startData.content_type,
        payload: copychiefOutput
      }
    }
  };
}

// Function to extract final copy from diagnostic report
function extractFinalCopy(payload) {
  if (typeof payload !== 'string') return payload;

  const finalCopyMatch = payload.match(/=== FINAL COPY ===\s*([\s\S]*?)$/);
  if (finalCopyMatch) {
    return finalCopyMatch[1].trim();
  }
  return payload;
}

// Function to extract diagnostic report
function extractDiagnosticReport(payload) {
  if (typeof payload !== 'string') return '';

  const reportMatch = payload.match(/=== COPY-CHIEF DIAGNOSTIC REPORT ===([\s\S]*?)=== FINAL COPY ===/);
  if (reportMatch) {
    return reportMatch[0].trim();
  }
  return '';
}

// Extract key information from webhook and start data
const clipId = startData.id || webhookData.recordId;
const brandInfo = webhookData.brand || {};
const variables = webhookData.variables || {};

// Process the content based on type
let processedContent = [];

if (startData.content_type === 'quotes') {
  // Handle quotes - create array for Split Out node
  const contents = parsedOutput.contents;
  Object.keys(contents).forEach(key => {
    const quote = contents[key];
    processedContent.push({
      session_id: parsedOutput.session_id,
      created_at: parsedOutput.created_at,
      final_copy: typeof quote === 'string' ? quote : quote.payload,
      diagnostic_report: '',
      content_type: 'quotes',
      original_content_key: key,

      // Core identifiers
      clip_id: clipId,
      brand_record_id: brandInfo.RECORD_ID || startData.Brand?.[0],
      brand_name: brandInfo.BRAND_EMAIL || brandInfo.NAME,

      // Airtable info
      body_variables_BASE_ID: variables.BASE_ID || startData.body_variables_BASE_ID,
      body_variables_CONTENT_TABLE: variables.CONTENT_TABLE || startData.body_variables_CONTENT_TABLE,

      // Folder information (will be populated from clip record fetch)
      folder_id: null, // To be filled from clip record
      parent_folder_id: brandInfo.GOOGLE_FOLDER_ROOT_ID,
      folder_url: null, // To be constructed after getting folder_id

      // Parent relationships  
      parent_video: null, // To be filled from clip record
      parent_idea: clipId, // The clip itself is the parent idea

      // Content metadata
      description: typeof quote === 'string' ? quote : quote.payload,

      // Pass through all Start data
      ...startData
    });
  });
} else {
  // Handle single content (posts, emails, etc.)
  const content1 = parsedOutput.contents.content1;
  const payload = content1.payload || content1;

  processedContent.push({
    session_id: parsedOutput.session_id,
    created_at: parsedOutput.created_at,
    final_copy: extractFinalCopy(payload),
    diagnostic_report: extractDiagnosticReport(payload),
    content_type: startData.content_type,
    original_content_key: 'content1',

    // Core identifiers
    clip_id: clipId,
    brand_record_id: brandInfo.RECORD_ID || startData.Brand?.[0],
    brand_name: brandInfo.BRAND_EMAIL || brandInfo.NAME,

    // Airtable info
    body_variables_BASE_ID: variables.BASE_ID || startData.body_variables_BASE_ID,
    body_variables_CONTENT_TABLE: variables.CONTENT_TABLE || startData.body_variables_CONTENT_TABLE,

    // Folder information (will be populated from clip record fetch)
    folder_id: null, // To be filled from clip record
    parent_folder_id: brandInfo.GOOGLE_FOLDER_ROOT_ID,
    folder_url: null, // To be constructed after getting folder_id

    // Parent relationships
    parent_video: null, // To be filled from clip record
    parent_idea: clipId, // The clip itself is the parent idea

    // Content metadata
    description: extractFinalCopy(payload),

    // Pass through all Start data
    ...startData
  });
}$('Get record').first().json['Folder ID']

// Return the processed content
return processedContent.map(item => ({ json: item }));

