// Get all input items (these are just file IDs)
const allItems = $input.all();

console.log(`Received ${allItems.length} file IDs from Google Drive`);

if (allItems.length === 0) {
    throw new Error("No file IDs received from Google Drive");
}

// Since we know these are all video files from the B-roll folder,
// we can directly select a random one
const randomIndex = Math.floor(Math.random() * allItems.length);
const selectedVideoId = allItems[randomIndex].json.id;

console.log(`Selected video ID: ${selectedVideoId}`);

// Build the video URL using the file ID
const videoUrl = `https://drive.google.com/uc?export=download&id=${selectedVideoId}`;

// Get the uploaded image URL from the previous node
let quoteImageUrl = '';
try {
    const uploadedImageData = $node["Google Drive: Upload Quote Image"].json;
    quoteImageUrl = uploadedImageData.webContentLink || `https://drive.google.com/uc?export=download&id=${uploadedImageData.id}`;
} catch (e) {
    throw new Error("Could not find uploaded image URL: " + e.message);
}

// Get other required data for the final Airtable update
const airtableData = $node["Get a record"].json;
const webhookData = $node["Webhook"].json;

console.log(`Video URL: ${videoUrl}`);
console.log(`Image URL: ${quoteImageUrl}`);

// Return the FFmpeg payload
return [{
    json: {
        ffmpeg_json: {
            id: "quote-overlay-video",
            inputs: [
                {
                    file_url: videoUrl,
                    options: [{ option: "-t", argument: 7 }]
                },
                {
                    file_url: quoteImageUrl
                }
            ],
            filters: [
                { filter: "[0:v]scale=1080:1920:force_original_aspect_ratio=decrease,pad=1080:1920:(ow-iw)/2:(oh-ih)/2:color=black[bg]" },
                { filter: "[1:v]scale=1080:1080[quote]" },
                { filter: "[bg][quote]overlay=(W-w)/2:(H-h)/2[outv]" }
            ],
            outputs: [
                {
                    options: [
                        { option: "-map", argument: "[outv]" },
                        { option: "-c:v", argument: "libx264" },
                        { option: "-preset", argument: "fast" },
                        { option: "-crf", argument: 23 },
                        { option: "-t", argument: 7 }
                    ]
                }
            ],
            metadata: {
                thumbnail: true,
                filesize: true,
                duration: true,
                bitrate: true,
                encoder: true
            }
        },
        // Include data for the final Airtable update
        baseId: webhookData.body.variables.BASE_ID,
        contentTable: webhookData.body.variables.CONTENT_TABLE,
        recordIdToUpdate: airtableData.id
    }
}];

// Enhanced Process Clip Timing - Updated for Title and Description format
const aiOutput = $input.first().json.output;

// Parse clips from AI output
const clips = [];

// Primary pattern for format with Title and Description on separate lines
let clipRegex = /Clip(\d+):\s*\n(?:Title:\s*(.*?)\s*\n)?(?:Description:\s*(.*?)\s*\n)?Clip Contents:\s*([\s\S]*?)(?=\s*Clip\d+:|$)/g;

let clipMatch;

while ((clipMatch = clipRegex.exec(aiOutput)) !== null) {
    clips.push({
        clip_number: parseInt(clipMatch[1].trim()),
        title: clipMatch[2] ? clipMatch[2].trim() : `Clip ${clipMatch[1]}`,
        description: clipMatch[3] ? clipMatch[3].trim() : '',
        ai_start_time: null,
        ai_end_time: null,
        full_transcript: clipMatch[4].trim(),
    });
}

// If no clips found, try format with Title/Description on same line as Clip number (fallback)
if (clips.length === 0) {
    clipRegex = /Clip(\d+):\s*Title:\s*(.*?)\s*\nDescription:\s*(.*?)\s*\nClip Contents:\s*([\s\S]*?)(?=\s*Clip\d+:|$)/g;

    while ((clipMatch = clipRegex.exec(aiOutput)) !== null) {
        clips.push({
            clip_number: parseInt(clipMatch[1].trim()),
            title: clipMatch[2].trim(),
            description: clipMatch[3].trim(),
            ai_start_time: null,
            ai_end_time: null,
            full_transcript: clipMatch[4].trim(),
        });
    }
}

// If still no clips, try old format with just Description (backward compatibility)
if (clips.length === 0) {
    clipRegex = /Clip(\d+):\s*Description:\s*(.*?)\s*\nClip Contents:\s*([\s\S]*?)(?=\s*Clip\d+:|$)/g;

    while ((clipMatch = clipRegex.exec(aiOutput)) !== null) {
        clips.push({
            clip_number: parseInt(clipMatch[1].trim()),
            title: clipMatch[2] ? clipMatch[2].trim() : `Clip ${clipMatch[1]}`,
            description: clipMatch[2] ? clipMatch[2].trim() : '',
            ai_start_time: null,
            ai_end_time: null,
            full_transcript: clipMatch[3].trim(),
        });
    }
}

console.log(`Found ${clips.length} clips in AI output`);

if (clips.length === 0) {
    return [{ json: { 
        error: 'No clips could be parsed from AI output',
        debug: {
            output_preview: aiOutput.substring(0, 500),
            output_length: aiOutput.length
        }
    } }];
}

// Get VTT content
const vttContent = $('Extract from File').first().json.vtt_content;

if (!vttContent || typeof vttContent !== 'string') {
    console.log('No VTT content found - cannot process clips without timing');
    return [];
}

console.log(`VTT content found, length: ${vttContent.length} characters`);

// Function to convert VTT time string to seconds
const timeToSeconds = (time) => {
    if (!time) return null;
    const cleanTime = time.replace(',', '.');
    const parts = cleanTime.split(':');
    if (parts.length === 3) {
        return parseFloat(parts[0]) * 3600 + parseFloat(parts[1]) * 60 + parseFloat(parts[2]);
    } else if (parts.length === 2) {
        return parseFloat(parts[0]) * 60 + parseFloat(parts[1]);
    }
    return parseFloat(time);
};

// Parse VTT blocks
const vttBlocks = [];
const vttRegex = /(?:(\d+)\s*\n\s*)?((\d{2}:\d{2}:\d{2}[,.]?\d{3})\s*-->\s*(\d{2}:\d{2}:\d{2}[,.]?\d{3}))\s*\n([\s\S]*?)(?=\n\n|\n\d|\nWEBVTT|\n$|$)/g;

let match;
let blockNumber = 1;
while ((match = vttRegex.exec(vttContent)) !== null) {
    const startSeconds = timeToSeconds(match[3]);
    const endSeconds = timeToSeconds(match[4]);
    vttBlocks.push({
        num: match[1] ? parseInt(match[1]) : blockNumber,
        startTime: match[3],
        endTime: match[4],
        startSeconds: startSeconds,
        endSeconds: endSeconds,
        duration: endSeconds - startSeconds,
        text: match[5].trim(),
        originalText: match[5].trim() // Keep original for exact matching
    });
    blockNumber++;
}

if (vttBlocks.length === 0) {
    console.log('Could not parse VTT blocks');
    return [];
}

console.log(`Parsed ${vttBlocks.length} VTT blocks`);

// Multi-level text cleaning functions
const cleanLevel1 = (text) => {
    // Minimal cleaning - preserve most structure
    return text
        .replace(/\r\n/g, '\n')
        .replace(/\r/g, '\n')
        .trim();
};

const cleanLevel2 = (text) => {
    // Remove speaker labels but preserve punctuation
    return text
        .replace(/^[^:]*:\s*/gm, '') // Remove speaker labels
        .replace(/\r\n/g, ' ')
        .replace(/\n/g, ' ')
        .replace(/\s+/g, ' ')
        .trim();
};

const cleanLevel3 = (text) => {
    // Aggressive cleaning for fuzzy matching
    return text
        .replace(/^[^:]*:\s*/gm, '') // Remove speaker labels
        .replace(/[^a-zA-Z0-9\s]/g, ' ') // Replace punctuation with spaces
        .replace(/\s+/g, ' ')
        .toLowerCase()
        .trim();
};

// Create multiple versions of VTT text for matching
const vttVersions = {
    original: vttBlocks.map(b => b.originalText).join(' '),
    level1: vttBlocks.map(b => cleanLevel1(b.text)).join(' '),
    level2: vttBlocks.map(b => cleanLevel2(b.text)).join(' '),
    level3: vttBlocks.map(b => cleanLevel3(b.text)).join(' ')
};

// Enhanced matching function
const findBestMatch = (clipText, vttVersions, vttBlocks) => {
    const strategies = [
        { name: 'exact', clean: cleanLevel1, vtt: vttVersions.original },
        { name: 'no-speaker', clean: cleanLevel2, vtt: vttVersions.level2 },
        { name: 'fuzzy', clean: cleanLevel3, vtt: vttVersions.level3 }
    ];

    for (const strategy of strategies) {
        const cleanClip = strategy.clean(clipText);
        const words = cleanClip.split(' ').filter(w => w.length > 0);

        if (words.length < 3) continue;

        // Try to find the longest matching phrase for start
        for (let phraseLength = Math.min(15, words.length); phraseLength >= 3; phraseLength--) {
            const phrase = words.slice(0, phraseLength).join(' ');
            const index = strategy.vtt.indexOf(phrase);

            if (index !== -1) {
                console.log(`Match found using ${strategy.name} strategy with ${phraseLength} words`);

                // Find the end of the clip
                const endWords = words.slice(-Math.min(10, words.length));
                let endIndex = -1;

                for (let endLength = Math.min(10, endWords.length); endLength >= 3; endLength--) {
                    const endPhrase = endWords.slice(-endLength).join(' ');
                    const searchSpace = strategy.vtt.substring(index + phrase.length);
                    const relativeEnd = searchSpace.indexOf(endPhrase);

                    if (relativeEnd !== -1) {
                        endIndex = index + phrase.length + relativeEnd + endPhrase.length;
                        break;
                    }
                }

                if (endIndex === -1) {
                    // Estimate end based on clip length
                    endIndex = Math.min(index + cleanClip.length, strategy.vtt.length);
                }

                return {
                    startIndex: index,
                    endIndex: endIndex,
                    strategy: strategy.name,
                    confidence: phraseLength / words.length
                };
            }
        }
    }

    return null;
};

// Process clips
const processedClips = [];
const failedClips = [];

for (const clip of clips) {
    try {
        const clipTranscript = clip.full_transcript || '';
        if (!clipTranscript || clipTranscript.length < 10) {
            console.log(`Skipping clip ${clip.clip_number}: transcript too short`);
            failedClips.push({
                clip_number: clip.clip_number,
                reason: 'Transcript too short',
                transcript_length: clipTranscript.length
            });
            continue;
        }

        let startTime, endTime;
        let startBlock = null, endBlock = null;
        let relevantBlocks = [];

        // Match text to find blocks
        const matchResult = findBestMatch(clipTranscript, vttVersions, vttBlocks);

        if (!matchResult) {
            console.log(`Skipping clip ${clip.clip_number}: could not match to VTT`);
            failedClips.push({
                clip_number: clip.clip_number,
                reason: 'Could not match to VTT',
                first_words: clipTranscript.split(' ').slice(0, 10).join(' ')
            });
            continue;
        }

        // Map indices to VTT blocks
        const cleanVtt = matchResult.strategy === 'fuzzy' ? vttVersions.level3 : 
                         matchResult.strategy === 'no-speaker' ? vttVersions.level2 : 
                         vttVersions.original;

        let accumulatedChars = 0;
        let charOffsetStart = 0, charOffsetEnd = 0;

        for (let i = 0; i < vttBlocks.length; i++) {
            const block = vttBlocks[i];
            const blockClean = matchResult.strategy === 'fuzzy' ? cleanLevel3(block.text) :
                              matchResult.strategy === 'no-speaker' ? cleanLevel2(block.text) :
                              block.text;
            const blockLength = blockClean.length + 1;

            if (!startBlock && (accumulatedChars + blockLength) > matchResult.startIndex) {
                startBlock = block;
                charOffsetStart = matchResult.startIndex - accumulatedChars;
            }
            if (!endBlock && (accumulatedChars + blockLength) > matchResult.endIndex) {
                endBlock = block;
                charOffsetEnd = matchResult.endIndex - accumulatedChars;
            }

            accumulatedChars += blockLength;
            if (startBlock && endBlock) break;
        }

        if (!startBlock || !endBlock || endBlock.startSeconds < startBlock.startSeconds) {
            console.log(`Skipping clip ${clip.clip_number}: invalid timing blocks`);
            failedClips.push({
                clip_number: clip.clip_number,
                reason: 'Invalid timing blocks'
            });
            continue;
        }

        // Calculate precise timing
        const startBlockTextClean = cleanLevel3(startBlock.text);
        const startRatio = Math.max(0, Math.min(1, charOffsetStart / (startBlockTextClean.length || 1)));
        startTime = startBlock.startSeconds + (startBlock.duration * startRatio);

        const endBlockTextClean = cleanLevel3(endBlock.text);
        const endRatio = Math.max(0, Math.min(1, charOffsetEnd / (endBlockTextClean.length || 1)));
        endTime = endBlock.startSeconds + (endBlock.duration * endRatio);

        // Get the VTT segment
        const startIndex = vttBlocks.findIndex(b => b.num === startBlock.num);
        const endIndex = vttBlocks.findIndex(b => b.num === endBlock.num);
        relevantBlocks = vttBlocks.slice(startIndex, endIndex + 1);

        const vttSegment = relevantBlocks.map(b => 
            `${b.num}\n${b.startTime} --> ${b.endTime}\n${b.text}`
        ).join('\n\n');

        // Validate duration
        const duration = endTime - startTime;
        if (duration <= 0 || duration > 180) {
            console.log(`Skipping clip ${clip.clip_number}: invalid duration ${duration.toFixed(2)}s`);
            failedClips.push({
                clip_number: clip.clip_number,
                reason: `Duration ${duration.toFixed(1)}s out of range`
            });
            continue;
        }

        // Build the output with ALL original fields preserved
        processedClips.push({
            json: {
                // Original clip data
                clip_number: clip.clip_number,
                title: clip.title,
                description: clip.description,
                full_transcript: clip.full_transcript,

                // Timing data
                startTime: startTime,
                endTime: endTime,
                srt_segment: vttSegment,

                // IMPORTANT: Block tracking fields
                startBlock: startBlock.num,
                endBlock: endBlock.num,
                blockCount: relevantBlocks.length,

                // Additional metadata
                duration: duration,
                timing_source: 'vtt_matched'
            }
        });

        console.log(`Processed clip ${clip.clip_number}: ${startTime.toFixed(2)}s - ${endTime.toFixed(2)}s (${duration.toFixed(1)}s), blocks ${startBlock.num}-${endBlock.num}`);

    } catch (error) {
        console.error(`Failed to process clip ${clip.clip_number}: ${error.message}`);
        failedClips.push({
            clip_number: clip.clip_number,
            title: clip.title,
            reason: error.message
        });
    }
}

// Summary
console.log(`\n=== PROCESSING SUMMARY ===`);
console.log(`Total clips from AI: ${clips.length}`);
console.log(`Successfully processed: ${processedClips.length}`);
console.log(`Failed to process: ${failedClips.length}`);

if (failedClips.length > 0) {
    console.log('\nFailed clips:');
    failedClips.forEach(f => {
        console.log(`  Clip ${f.clip_number}: ${f.reason}`);
    });
}

if (processedClips.length === 0) {
    return [{ json: { 
        error: 'No clips could be processed with timing',
        total_clips_from_ai: clips.length,
        failed_clips: failedClips,
        debug: {
            vtt_blocks: vttBlocks.length,
            first_clip_text: clips[0]?.full_transcript?.substring(0, 200)
        }
    } }];
}

// Return processed clips
return processedClips;

// Fixed Build Cut Command Code - Optimized for Highest Quality
const allPayloads = [];

console.log("Starting Build Cut Command");
console.log("Input items count:", $input.all().length);

// Get callback URL from webhook payload
let callbackUrl = "";
try {
  const webhookData = $('Variables').first().json.webhook_body;
  callbackUrl = webhookData.action.CALLBACK_WEBHOOK_URL;
  console.log("Callback URL from webhook:", callbackUrl);
} catch (error) {
  console.error("Error accessing webhook data:", error.message);
  return [];
}

// Validate we have callback URL
if (!callbackUrl) {
  console.error("No callback URL found");
  return [];
}

console.log("Processing", $input.all().length, "clip records from Airtable");

// Process each clip record
for (let i = 0; i < $input.all().length; i++) {
  try {
    const item = $input.all()[i];
    const clipRecord = item.json;

    console.log(`Processing Airtable record ${i + 1}:`, clipRecord.id);
    console.log("Available fields:", Object.keys(clipRecord));

    // FIXED: Construct video URL correctly using the actual workflow data structure
    let videoUrl = "";
    try {
      // First, try to get the MinIO base URL from Variables node (webhook data)
      let minioBaseUrl = "";
      const variablesData = $('Variables').first().json;

      // Check if MINIO_STORAGE_URL exists in webhook variables
      if (variablesData.webhook_body?.variables?.MINIO_STORAGE_URL) {
        minioBaseUrl = variablesData.webhook_body.variables.MINIO_STORAGE_URL;
        console.log("MinIO Base URL from webhook:", minioBaseUrl);
      } else {
        // Fallback to known MinIO URL structure
        minioBaseUrl = 'https://minio-api.everydaycreator.org/nca/';
        console.log("Using default MinIO Base URL:", minioBaseUrl);
      }

      // Get the video file ID from the clip record
      let videoFileId = "";

      // Check various possible field names in the Airtable record
      if (clipRecord['Shared screen w/ speaker video gdrive id (from Parent AV)']) {
        // This is an array field, get the first element
        videoFileId = Array.isArray(clipRecord['Shared screen w/ speaker video gdrive id (from Parent AV)']) 
          ? clipRecord['Shared screen w/ speaker video gdrive id (from Parent AV)'][0]
          : clipRecord['Shared screen w/ speaker video gdrive id (from Parent AV)'];
      } else if (clipRecord['Shared screen w/ speaker video gdrive id']) {
        videoFileId = clipRecord['Shared screen w/ speaker video gdrive id'];
      } else if (variablesData.airtable_record?.['Shared screen w/ speaker video gdrive id']) {
        // Try to get from the original Variables node data
        videoFileId = variablesData.airtable_record['Shared screen w/ speaker video gdrive id'];
      }

      if (!videoFileId) {
        // Use the example ID as last resort
        videoFileId = '1T-MBjIlYGHgp0dvB6bFAETYK1dZI7Dkb';
        console.log("Warning: Using fallback video file ID");
      }

      // Construct the full video URL
      // MinIO URLs need file extension for FFmpeg to work
      // Check if we need to add extension
      if (!videoFileId.includes('.')) {
        // Assume MP4 format for video files
        videoFileId = videoFileId + '.mp4';
      }

      videoUrl = minioBaseUrl.endsWith('/') 
        ? `${minioBaseUrl}${videoFileId}`
        : `${minioBaseUrl}/${videoFileId}`;

      console.log("Video File ID (with extension):", videoFileId);
      console.log("Constructed video URL:", videoUrl);

    } catch (urlError) {
      console.error("Error constructing video URL:", urlError.message);

      // Final fallback: use the example URL structure with .mp4 extension
      videoUrl = 'https://minio-api.everydaycreator.org/nca/1T-MBjIlYGHgp0dvB6bFAETYK1dZI7Dkb.mp4';
      console.log("Using complete fallback URL with extension:", videoUrl);
    }

    if (!videoUrl) {
      console.error(`No video URL constructed for record ${clipRecord.id}`);
      continue;
    }

    // Extract timing data from Airtable record
    const startTimeFromRecord = clipRecord['Start Time'];
    const endTimeFromRecord = clipRecord['End Time'];
    const clipTitle = clipRecord['Title'] || clipRecord['Description'] || 'Untitled Clip';

    console.log(`Record timing: Start=${startTimeFromRecord}, End=${endTimeFromRecord}`);

    // Validate required fields
    if (startTimeFromRecord === undefined || startTimeFromRecord === null) {
      console.error(`Start Time field missing in record ${clipRecord.id}`);
      continue;
    }
    if (endTimeFromRecord === undefined || endTimeFromRecord === null) {
      console.error(`End Time field missing in record ${clipRecord.id}`);
      continue;
    }

    // Calculate clip boundaries with small buffer
    const startTime = Math.max(0, startTimeFromRecord - 0.2);
    const endTime = endTimeFromRecord + 0.2;
    const duration = endTime - startTime;

    if (duration <= 0) {
      console.error(`Invalid duration (${duration.toFixed(2)}s) for record ${clipRecord.id}`);
      continue;
    }

    console.log(`Record ${clipRecord.id} final timing - Start: ${startTime.toFixed(3)}s, End: ${endTime.toFixed(3)}s, Duration: ${duration.toFixed(3)}s`);

    // Configure FFmpeg inputs according to API documentation
    const inputs = [{
      file_url: videoUrl,
      options: [
        { option: '-f', argument: 'mp4' },  // Explicitly specify input format
        { option: '-ss', argument: startTime.toFixed(3) },
        { option: '-t', argument: duration.toFixed(3) }
      ]
    }];

    // Configure output options - OPTIMIZED FOR HIGHEST QUALITY
    const outputOptions = [];
    outputOptions.push(
      { option: '-map', argument: '0:v' },  // Map video stream
      { option: '-map', argument: '0:a' },  // Map audio stream
      { option: '-c:v', argument: 'libx264' },  // H.264 video codec
      { option: '-preset', argument: 'slow' },  // Changed from 'fast' to 'slow' for better quality
      { option: '-crf', argument: 18 },  // Lowered from 23 to 18 for higher quality (visually lossless)
      { option: '-profile:v', argument: 'high' },  // Use H.264 high profile for better quality
      { option: '-level', argument: '4.2' },  // H.264 level for compatibility
      { option: '-pix_fmt', argument: 'yuv420p' },  // Pixel format for compatibility
      { option: '-tune', argument: 'film' },  // Tune for high quality video content
      { option: '-c:a', argument: 'aac' },  // AAC audio codec
      { option: '-b:a', argument: '256k' },  // Higher audio bitrate (increased from default ~128k)
      { option: '-ar', argument: '48000' },  // Audio sample rate at 48kHz
      { option: '-movflags', argument: '+faststart' },  // Optimize for web streaming
      { option: '-max_muxing_queue_size', argument: '9999' }  // Prevent muxing queue issues
    );

    // Create the complete FFmpeg payload according to API docs
    const ffmpegPayload = {
      id: clipRecord.id || `clip_${i + 1}_${Date.now()}`,
      inputs: inputs,
      outputs: [{
        options: outputOptions
      }],
      webhook_url: callbackUrl,
      metadata: {
        thumbnail: true,
        filesize: true,
        duration: true,
        bitrate: true,
        encoder: true
      }
      // NO FILTERS PROPERTY - no fades
    };

    // Return the payload in the format the HTTP Request node expects
    allPayloads.push({
      json: {
        ffmpegPayload: ffmpegPayload,
        clipInfo: {
          id: clipRecord.id,
          description: clipTitle,
          duration: duration,
          startTime: startTime,
          endTime: endTime
        }
      }
    });

    console.log(`Successfully prepared record ${clipRecord.id}`);
    console.log(`Video URL being used: ${videoUrl}`);

  } catch (error) {
    console.error(`Error processing record ${i + 1}:`, error.message);
    console.error("Stack:", error.stack);
    continue;
  }
}

console.log(`Prepared ${allPayloads.length} clips for processing`);

// Log first payload for debugging
if (allPayloads.length > 0) {
  console.log("Sample ffmpegPayload:", JSON.stringify(allPayloads[0].json.ffmpegPayload, null, 2));
}

return allPayloads;

