function parsePrompts(content) {
  const prompts = [];

  // Split by prompt start markers (with ID)
  const promptBlocks = content.split(/=== PROMPT START ID:\d+ ===/);

  // Skip the first element if it's empty (before the first prompt)
  const blocksToProcess = promptBlocks.filter(block => block.trim().length > 0);

  for (const block of blocksToProcess) {
    try {
      // Find the JSON block - it appears right after the newline following the prompt start
      // The JSON is NOT wrapped in backticks, it's just plain JSON
      const jsonMatch = block.match(/^\s*(\{[\s\S]*?\})\s*(?:PROMPT CONTENT:|$)/m);
      if (!jsonMatch) {
        console.log('No JSON metadata found in block');
        continue;
      }

      // Parse the JSON metadata
      let metadata;
      try {
        metadata = JSON.parse(jsonMatch[1]);
      } catch (jsonError) {
        console.log('Failed to parse JSON metadata:', jsonError.message);
        continue;
      }

      // Extract prompt content
      const promptContentMatch = block.match(/PROMPT CONTENT:\s*([\s\S]*?)\s*=== PROMPT END ===/);
      if (!promptContentMatch) {
        console.log('No prompt content found in block');
        continue;
      }

      // Build the prompt object
      const prompt = {
        id: metadata.id,
        title: metadata.title || '',
        job_role: metadata.job_role || '',
        content_type: metadata.content_type || '',
        prompt: promptContentMatch[1].trim()
      };

      // Only add if we have all required fields
      if (prompt.id && prompt.job_role && prompt.prompt) {
        prompts.push(prompt);
      }

    } catch (error) {
      console.log('Error parsing prompt block:', error.message);
      continue;
    }
  }

  return prompts;
}

