// Helper function to convert SRT time to seconds
const timeToSeconds = (time) => {
    const parts = time.replace(',', '.').split(':');
    return parseFloat(parts[0]) * 3600 + parseFloat(parts[1]) * 60 + parseFloat(parts[2]);
};

// Parse SRT blocks if available (optional - for validation or future use)
const srtBlocks = [];
if (srtContent) {
    const srtRegex = /(\d+)\s*\n\s*(\d{2}:\d{2}:\d{2}[,.]?\d{3})\s*-->\s*(\d{2}:\d{2}:\d{2}[,.]?\d{3})\s*\n([\s\S]*?)(?=\n\n|\n$|$)/g;
    let match;
    while ((match = srtRegex.exec(srtContent)) !== null) {
        const startSeconds = timeToSeconds(match[2]);
        const endSeconds = timeToSeconds(match[3]);
        srtBlocks.push({
            num: parseInt(match[1]),
            startTime: match[2],
            endTime: match[3],
            startSeconds: startSeconds,
            endSeconds: endSeconds,
            duration: endSeconds - startSeconds,
            text: match[4].trim()
        });
    }
    console.log(`Parsed ${srtBlocks.length} SRT blocks for validation`);
} else {
