// Get the transcript content
const vttContent = $('Text Convert1').item.json.output || '';
const folderData = $input.item.json;

// Convert VTT to SRT format
function convertVttToSrt(vttText) {
  // Split into lines and remove empty lines
  const lines = vttText.split('\n').filter(line => line.trim());

  let srtContent = '';
  let counter = 1;

  // Process each speaker block
  const speakerBlocks = vttText.split(/\n\n+/).filter(block => block.trim());

  speakerBlocks.forEach((block) => {
    if (!block.trim()) return;

    // Parse speaker and text
    const match = block.match(/^([^:]+):\s*(.*)$/s);
    if (match) {
      const speaker = match[1].trim();
      const text = match[2].trim().replace(/\n/g, ' ');

      // Create SRT format with dummy timestamps (adjust if you have actual timestamps)
      const startTime = `00:${String(counter).padStart(2, '0')}:00,000`;
      const endTime = `00:${String(counter).padStart(2, '0')}:30,000`;

      srtContent += `${counter}\n`;
      srtContent += `${startTime} --> ${endTime}\n`;
      srtContent += `${speaker}: ${text}\n\n`;

      counter++;
    }
  });

  return srtContent.trim();
}

// Convert VTT to SRT
const srtContent = convertVttToSrt(vttContent);

// Get meeting data for file naming
const meetingData = $('Process AI Classification').item.json;

// Return both transcript formats
return {
  json: {
    folderId: folderData.id,
    folderLink: folderData.webViewLink,
    srtContent: srtContent,
    txtContent: vttContent,
    srtFileName: `transcript_${meetingData.dateString}_${meetingData.timeString}.srt`,
    txtFileName: `transcript_${meetingData.dateString}_${meetingData.timeString}.txt`,
    airtableRecordId: $('Airtable').item.json.id
  }
};

// Get folder and transcript doc info
const folderData = $input.item.json;
const meetingData = $('Process AI Classification').item.json;

// Get SRT and TXT document IDs from previous nodes
const srtDocId = $('Create SRT Document').item.json.id;
const txtDocId = $('Create TXT Document').item.json.id;

// Return each video file for upload
const videoFiles = meetingData.files.filter(file => 
  file.recordingType !== 'audio_transcript'
);

return videoFiles.map(file => ({
  json: {
    downloadUrl: file.downloadUrl,
    fileName: file.fileName,
    folderId: folderData.folderId,
    folderLink: folderData.folderLink,
    airtableRecordId: folderData.airtableRecordId,
    srtDocId: srtDocId,
    txtDocId: txtDocId,
    totalFiles: videoFiles.length
  }
}));

// Aggregate all upload results
const uploads = $input.all();
const firstItem = uploads[0]?.json || {};

// Count successful uploads
const successCount = uploads.filter(item => !item.error).length;

// Get document links
const srtDocLink = `https://docs.google.com/document/d/${firstItem.srtDocId}`;
const txtDocLink = `https://docs.google.com/document/d/${firstItem.txtDocId}`;

return {
  json: {
    airtableRecordId: firstItem.airtableRecordId,
    folderLink: firstItem.folderLink,
    srtDocLink: srtDocLink,
    txtDocLink: txtDocLink,
    uploadStatus: `${successCount}/${firstItem.totalFiles} files uploaded`,
    allUploadsSuccessful: successCount === firstItem.totalFiles
  }
};

